public List<List<Integer>> threeSum (int[] nums){
	Set<List<Integer>> lists = new HashSet<>();
	Arrays.sort(nums);
	for (int start = 0; start < nums.length-2; start++) {
		int mid = start+1, end = nums.length-1;
		while(mid < end) {
			int sum = nums[start] + nums[mid] + nums[end];
			if(sum < 0) mid++;
			else if(sum > 0) end--;
