	String num = countAndSay(n - 1);

	int count = 0;
	char curr = num.charAt(0);
	StringBuilder sb = new StringBuilder();

	for (char ch : num.toCharArray()) {
		if (ch == curr) count++;
		else {
			sb.append(count).append(curr) ;
			count = 1; curr = ch;
		}
	}
