// naive approach O(n) space
int findNumber(int* arr, int n) {
    // create freq arr of positive number
    const int N = 1e6 + 2;
    bool freq_arr[N] = {false};
    for (int i = 0; i < n; i++) {
        if (arr[i] > 0)
            freq_arr[arr[i]] = true;
