ListNode merge(ListNode a, ListNode b) {
	if(a == null) return b;
	if(b == null) return a;

	ListNode smaller, larger;

	smaller = (a.val <= b.val) ? a : b;
	larger = (b.val >= a.val) ? b : a;

	smaller.next = merge(smaller.next, larger);
	return smaller;
}
